import { green } from 'ansi-colors'
import React from 'react'
import { View, Text, StyleSheet, ImageBackground, TouchableOpacity } from 'react-native'
import greenBg from '../../assets/greenBg.png'



const PostThumbnail = ({item={title: 'TITLE'}, setAppState}) => {
    return (
        <TouchableOpacity style={styles.container} onPress={()=>{setAppState('reader'); console.log('normal')}}>
            <ImageBackground source={greenBg} imageStyle={{borderRadius: 10}} style={{flexDirection: 'column-reverse', flex: 1}} resizeMode="cover" >
            <View style={styles.titleContainer}>
                <Text style={{fontWeight: 'bold', fontSize: 20}}>{item.title}</Text>
            </View>
            </ImageBackground>
        </TouchableOpacity>
    )
}

export default PostThumbnail

const styles = StyleSheet.create({
    container: {
        minHeight: 200,
        margin: 20,
        shadowOffset: {
            width: 1,
            height: 1
          },
          shadowOpacity: 0.8,
          shadowRadius: 4
    },
    titleContainer: {
        minHeight: 70,
        backgroundColor: 'rgba(255,255,255, 0.9)',
        padding: 20,
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,

    }

})
