import React, {useState} from 'react'
import { Text, StyleSheet, TouchableOpacity, Modal } from 'react-native'


const WordOftheDay = () => {

    const [isVisible, setIsVisible] = useState(false)

    return (
        <TouchableOpacity style={styles.container} onPress={()=>setIsVisible(true)}>
            <Modal animationType="slide" visible={isVisible} >
                <TouchableOpacity onPress={()=>setIsVisible(false)}>
                    <Text style={{backgroundColor: 'red', margin: 7, width: 10}}>X</Text>
                </TouchableOpacity>
                <Text>OK</Text>
            </Modal>
            <Text style={{fontWeight: 'bold', fontSize:40,}}>Word of the Day</Text>
            <Text style={{fontSize: 30, padding: 20, fontStyle: 'italic'}}>enjwen</Text>
            <Text style={{fontSize: 17, color: '#3daeff', padding: 0, fontStyle: 'italic', textDecorationLine: 'underline'}}>Learn more</Text>

        </TouchableOpacity>
    )
}

export default WordOftheDay

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white',
        borderRadius: 30,
        marginTop: 40,
        margin: 10,
        shadowOffset: {
            width: 1,
            height: 1
          },
          shadowOpacity: 0.8,
          shadowRadius: 3,
        padding: 20,
    }
})
