import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { BottomNavigation} from 'react-native-paper';
import HomePage from './src/navigation/HomePage';

const homePage = (setAppState) => <HomePage setAppState={setAppState}/>;

const AlbumsRoute = () => <Text>Albums</Text>;

const RecentsRoute = () => <Text>Recents</Text>;

const App = () => {
  const [index, setIndex] = useState(0);
  const [appSate, setAppState] = useState('normal') //normal or reader (when looking at a post)
  const [routes] = useState([
    { key: 'home', title: 'Home', icon: 'album' },
    { key: 'explore', title: 'Explore', icon: 'album' },
    { key: 'profile', title: 'Profile', icon: 'history' },
  ]);

  const renderScene = BottomNavigation.SceneMap({
    home: homePage(setAppState),
    explore: AlbumsRoute,
    profile: RecentsRoute,
  });

  if (appSate === 'normal'){
    return (
      <BottomNavigation
        barStyle={{backgroundColor: '#4287f5'}}
        navigationState={{ index, routes }}
        onIndexChange={setIndex}
        renderScene={renderScene}
      />
    )
  }
};

export default App
